import React, { useState, useEffect } from 'react';

// Memoized Cell Component for Performance
const GridCell = React.memo(({
    row, col,
    value,
    isBlack,
    number,
    isActive,
    onClick
}) => {
    // If it's a black cell, render simplistic
    if (isBlack) {
        return <div className="grid-cell black"></div>;
    }

    return (
        <div
            className={`grid-cell white ${isActive ? 'selected' : ''}`}
            onClick={() => onClick(row, col)}
        >
            {number && <span className="cell-number">{number}</span>}
            {value}
        </div>
    );
});

const CrosswordGrid = () => {
    // Grid Size (15x15 standard)
    const ROWS = 15;
    const COLS = 15;

    // State
    const [grid, setGrid] = useState([]);
    const [activeCell, setActiveCell] = useState(null); // { r, c }

    // Initialize Mock Grid
    useEffect(() => {
        const newGrid = [];
        for (let r = 0; r < ROWS; r++) {
            const row = [];
            for (let c = 0; c < COLS; c++) {
                // Mock pattern: some random black cells to look like a crossword
                // In real app, this comes from puzzle data
                const isBlack = Math.random() > 0.8;
                row.push({
                    r, c,
                    value: '',
                    isBlack: isBlack,
                    number: (r * COLS + c) % 7 === 0 && !isBlack ? (r * COLS + c) : null // Mock numbers
                });
            }
            newGrid.push(row);
        }
        setGrid(newGrid);
    }, []);

    const handleCellClick = React.useCallback((r, c) => {
        // Use functional state update or ref if grid changes, but grid is only set once here.
        // If grid changes later, add it to dependency. 
        // For now, grid is stable after mount.
        setGrid(currentGrid => {
            if (currentGrid[r][c].isBlack) return currentGrid;
            setActiveCell({ r, c });
            return currentGrid;
        });
    }, []);

    if (grid.length === 0) return <div>Loading Grid...</div>;

    return (
        <div className="crossword-grid">
            {grid.map((row, r) => (
                row.map((cell, c) => (
                    <GridCell
                        key={`${r}-${c}`}
                        row={r}
                        col={c}
                        value={cell.value}
                        isBlack={cell.isBlack}
                        number={cell.number}
                        isActive={activeCell && activeCell.r === r && activeCell.c === c}
                        onClick={handleCellClick}
                    />
                ))
            ))}
        </div>
    );
};

export default CrosswordGrid;
