import React, { useState } from 'react';
import './LobbyScreen.css';
import copyIcon from '../assets/copy_icon.png';

const LobbyScreen = ({
    sessionData = {
        host: 'Crouton',
        duration: '10 Minutes',
        gameMode: 'Teams',
        sessionId: '20261220142836COMPCROUTON',
        teamCount: 2, // 2, 3, or 4 teams
        maxPlayers: 30,
        minPlayers: 2
    },
    currentUser = 'Crouton',
    onStartGame,
    onGoBack
}) => {
    // Mock player data - replace with real data from socket
    const [players, setPlayers] = useState([
        'MONSTER18', 'CRYPTOCAT', 'D1OL', 'METIL', 'CRYPTOHOLIC',
        'CROUTON', 'NOOXIN', 'ANHELINK'
    ]);

    const [selectedTeam, setSelectedTeam] = useState(null);
    const [teamAssignments, setTeamAssignments] = useState({
        1: ['MONSTER18', 'CRYPTOCAT', 'D1OL'],
        2: ['METIL', 'CRYPTOHOLIC'],
        3: ['CROUTON'],
        4: ['NOOXIN', 'ANHELINK']
    });

    const isHost = currentUser === sessionData.host;
    const isTeamsMode = sessionData.gameMode === 'Teams';

    // Calculate max players per team based on team count
    const maxPerTeam = Math.floor(sessionData.maxPlayers / sessionData.teamCount);

    // Generate team array based on teamCount
    const teams = Array.from({ length: sessionData.teamCount }, (_, i) => ({
        id: i + 1,
        name: `Team ${String.fromCharCode(65 + i)}`, // A, B, C, D
        players: teamAssignments[i + 1] || [],
        maxPlayers: maxPerTeam
    }));

    const handleTeamSelect = (teamId) => {
        const team = teams.find(t => t.id === teamId);
        if (team.players.length >= team.maxPlayers) {
            alert(`Team ${team.name} is full (${team.maxPlayers}/${team.maxPlayers})`);
            return;
        }
        setSelectedTeam(teamId);
        // TODO: Send team selection to server
    };

    const copySessionId = () => {
        navigator.clipboard.writeText(sessionData.sessionId);
        // TODO: Show copied notification
    };

    return (
        <div className="lobby-overlay fade-in">
            <div className="lobby-container">
                {/* Header */}
                <div className="lobby-header">
                    <h1>WAITING LOBBY</h1>
                </div>

                {/* Session Info Grid */}
                <div className="session-info-grid">
                    <div className="info-field">
                        <label>Session Host</label>
                        <input type="text" value={sessionData.host} readOnly />
                    </div>
                    <div className="info-field">
                        <label>Time Duration</label>
                        <input type="text" value={sessionData.duration} readOnly />
                    </div>
                    <div className="info-field">
                        <label>Player Count</label>
                        <input
                            type="text"
                            value={`${sessionData.minPlayers} Minimum, ${sessionData.maxPlayers} Maximum`}
                            readOnly
                        />
                    </div>
                    <div className="info-field">
                        <label>Game Mode</label>
                        <input type="text" value={sessionData.gameMode} readOnly />
                    </div>
                    <div className="info-field session-id-field">
                        <label>Session ID</label>
                        <div className="session-id-wrapper">
                            <input type="text" value={sessionData.sessionId} readOnly />
                            <button className="copy-btn" onClick={copySessionId}>
                                <img
                                    src={copyIcon}
                                    alt="Copy"
                                    style={{ width: '16px', height: '16px', objectFit: 'contain' }}
                                />
                            </button>
                        </div>
                    </div>
                </div>

                {/* Team Selection (only for Teams mode) */}
                {isTeamsMode && (
                    <div className="team-selection">
                        <h3>SELECT YOUR TEAM</h3>
                        <div className="teams-grid">
                            {teams.map(team => (
                                <div
                                    key={team.id}
                                    className={`team-card ${selectedTeam === team.id ? 'selected' : ''} ${team.players.length >= team.maxPlayers ? 'full' : ''}`}
                                    onClick={() => handleTeamSelect(team.id)}
                                >
                                    <div className="team-name">{team.name}</div>
                                    <div className="team-count">
                                        {team.players.length}/{team.maxPlayers} Players
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Joined Players List */}
                <div className="players-section">
                    <div className="players-header">
                        JOINED PLAYERS ({players.length}/{sessionData.maxPlayers})
                    </div>
                    <div className="players-list">
                        {players.map((player, index) => (
                            <span key={index} className="player-tag">
                                {player}
                            </span>
                        ))}
                    </div>
                </div>

                {/* Action Buttons */}
                <div className="lobby-footer">
                    {isHost && (
                        <button
                            className="action-btn primary"
                            onClick={onStartGame}
                            disabled={players.length < sessionData.minPlayers}
                        >
                            START GAME
                        </button>
                    )}
                    <button className="action-btn secondary" onClick={onGoBack}>
                        GO BACK
                    </button>
                </div>
            </div>
        </div>
    );
};

export default LobbyScreen;
