import React from 'react';
import './MenuScreen.css';

const MenuScreen = ({ onStartOnboarding }) => {
    return (
        <div className="menu-screen">
            <h1 className="menu-title">HOGWASH WORDPLAY</h1>
            <p className="menu-subtitle">CONNECT. COMPETE. CONQUER.</p>

            <div className="menu-options">
                <button className="menu-button" onClick={onStartOnboarding}>
                    Start Game
                </button>

                <button className="menu-button secondary">
                    Join Session
                </button>

                <button className="menu-button secondary" style={{ fontSize: '0.8rem', padding: '0.8rem' }}>
                    How to Play
                </button>
            </div>

            <div style={{ position: 'absolute', bottom: '20px', fontSize: '0.7rem', opacity: 0.3 }}>
                v0.1.0 â€¢ PRE-ALPHA
            </div>
        </div>
    );
};

export default MenuScreen;
