import React from 'react';
import './GameScreen.css';

// Assets
import soundIcon from '../assets/sound_icon.png';
import homeIcon from '../assets/home_icon.png';
import infoIcon from '../assets/info_icon.png';
// Using restart icon as a placeholder for "edit/pencil" if needed, or similar
import restartIcon from '../assets/restart_icon.png';

const GameHeader = ({ time = "00:00:17", hintsLeft = 5 }) => {
    return (
        <div className="game-header">
            <div className="header-left">
                <div className="time-badge">
                    <span className="badge-label">Time</span>
                    <span className="badge-value">{time}</span>
                </div>

                <div className="hint-badge">
                    <span className="badge-label">Hint</span>
                    <span className="badge-value">{hintsLeft} Left</span>
                </div>
            </div>

            <div className="header-right">
                <button className="icon-btn"><img src={soundIcon} alt="Sound" /></button>
                <button className="icon-btn"><img src={homeIcon} alt="Home" /></button>
                <button className="icon-btn"><img src={infoIcon} alt="Info" /></button>
                <button className="icon-btn"><img src={restartIcon} alt="Edit" /></button>
            </div>
        </div>
    );
};

export default GameHeader;
