import React, { useEffect, useRef, useState } from 'react';
import './SplashScreen.css';

const SplashScreen = ({ onComplete }) => {
    // --- CONFIGURATION ZONE ---
    // Change these values to tweak the look!

    // 1. Pixel Size: Lower number = Big Blocky Pixels. Higher = Smooth/Small pixels.
    const COLUMNS = 9;
    const ROWS = 6;

    // 2. Wave Speed: Time in ms between each ring of pixels. Lower = Faster Wave.
    const WAVE_DELAY_MS = 60;

    // 3. Colors: Add or remove hex codes here to change the palette.
    // 3. Colors: Purple/Blue Glitch Palette
    const PALETTE = ['#7B1FA2', '#4A148C', '#AB47BC', '#8E24AA', '#536DFE'];

    // Generate the grid data once
    const pixels = React.useMemo(() => {
        const grid = [];
        const centerRow = ROWS / 2;
        const centerCol = COLUMNS / 2;
        const totalMaxDist = Math.sqrt(Math.pow(COLUMNS / 2, 2) + Math.pow(ROWS / 2, 2));

        for (let i = 0; i < COLUMNS * ROWS; i++) {
            const row = Math.floor(i / COLUMNS);
            const col = i % COLUMNS;

            // Distance Logic
            const deltaX = col - centerCol;
            const deltaY = row - centerRow;
            const dist = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

            // "Glitch Wave" Logic (Concentric Rings)
            // Ring Size: How thick the bands are
            const ringSize = 1.8;
            const band = Math.floor(dist / ringSize);

            // Visible if it's the Center (band 0) OR a specific outer ring (band 2)
            // This creates: [Center Cluster] -- [Dark Gap] -- [Outer Ring Cluster]
            const isVisible = (band === 0) || (band === 2) || (band === 4);

            let color = 'transparent';
            let popDelay = '0ms';

            if (isVisible) {
                color = PALETTE[Math.floor(Math.random() * PALETTE.length)];
                popDelay = `${dist * WAVE_DELAY_MS}ms`;
            }

            const exitDelay = dist * WAVE_DELAY_MS;

            grid.push({
                id: i,
                color: color,
                popDelay: popDelay,
                exitDelay: `${exitDelay}ms`,
                key: i
            });
        }
        return { grid, maxDelay: totalMaxDist * WAVE_DELAY_MS };
    }, []);

    useEffect(() => {
        const WAIT_TIME = 3000;
        const ANIMATION_DURATION = 600; // Match CSS

        const exitTimer = setTimeout(() => {
            setExiting(true);
        }, WAIT_TIME);

        const completeTimer = setTimeout(() => {
            if (onComplete) onComplete();
        }, WAIT_TIME + pixels.maxDelay + ANIMATION_DURATION + 500);

        return () => {
            clearTimeout(exitTimer);
            clearTimeout(completeTimer);
        };
    }, [onComplete, pixels.maxDelay]);

    return (
        <div className="splash-screen-container">
            <div
                id="pixel-canvas"
                style={{ gridTemplateColumns: `repeat(${COLUMNS}, 1fr)` }}
            >
                {pixels.grid.map((p) => (
                    <div
                        key={p.key}
                        className={`pixel ${exiting ? 'exiting' : ''}`}
                        style={{
                            backgroundColor: p.color, // Initial color
                            '--pixel-color': p.color, // For CSS var usage
                            '--pop-delay': p.popDelay,
                            '--exit-delay': p.exitDelay
                        }}
                    ></div>
                ))}
            </div>
        </div>
    );
};

export default SplashScreen;
