import React from 'react';
import './CluePanel.css';

function CluePanel({ crosswordData, selectedWord, onClueClick }) {
    if (!crosswordData || !crosswordData.words) {
        return <div>Loading clues...</div>;
    }

    const { words } = crosswordData;

    const acrossWords = words.filter(w => w.direction === 'across');
    const downWords = words.filter(w => w.direction === 'down');

    const renderClue = (word) => {
        const isSelected = selectedWord &&
            selectedWord.number === word.number &&
            selectedWord.direction === word.direction;

        return (
            <div
                key={`${word.number}-${word.direction}`}
                className={`clue-item ${isSelected ? 'selected' : ''}`}
                onClick={() => onClueClick(word)}
            >
                <span className="clue-number">{word.number}.</span>
                <span className="clue-text">{word.clue}</span>
            </div>
        );
    };

    return (
        <div className="clue-panel">
            <div className="clue-section">
                <h3 className="clue-heading">Across</h3>
                <div className="clue-list">
                    {acrossWords.map(renderClue)}
                </div>
            </div>

            <div className="clue-section">
                <h3 className="clue-heading">Down</h3>
                <div className="clue-list">
                    {downWords.map(renderClue)}
                </div>
            </div>
        </div>
    );
}

export default CluePanel;
