import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import './CreateSession.css';

function CreateSession() {
    const navigate = useNavigate();
    const location = useLocation();
    const { emit, connected } = useSocket();

    const username = location.state?.username || 'Player';

    const [mode, setMode] = useState('competitive');
    const [timerDuration, setTimerDuration] = useState(10);
    const [wordsInput, setWordsInput] = useState('HELLO|A greeting\nWORLD|Our planet\nPUZZLE|A game to solve\nCROSSWORD|Word game with clues');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleCreate = () => {
        setError('');
        setLoading(true);

        // Parse words and clues
        const lines = wordsInput.trim().split('\n').filter(line => line.trim());
        const words = [];
        const clues = [];

        for (const line of lines) {
            const parts = line.split('|');
            if (parts.length !== 2) {
                setError('Each line must be in format: WORD|Clue');
                setLoading(false);
                return;
            }
            words.push(parts[0].trim().toUpperCase());
            clues.push(parts[1].trim());
        }

        if (words.length < 3) {
            setError('Please provide at least 3 words');
            setLoading(false);
            return;
        }

        if (words.length > 30) {
            setError('Maximum 30 words allowed');
            setLoading(false);
            return;
        }

        emit('create-session', {
            hostId: Date.now().toString(),
            mode,
            words,
            clues,
            timerDuration,
            username,
        }, (response) => {
            setLoading(false);
            if (response.success) {
                navigate(`/lobby/${response.session.sessionId}`, {
                    state: { username, isHost: true, session: response.session },
                });
            } else {
                setError(response.error || 'Failed to create session');
            }
        });
    };

    return (
        <div className="page-container">
            <div className="page-header">
                <h1 className="page-title">Create Game Session</h1>
                <p className="page-subtitle">Configure your crossword game</p>
            </div>

            <div className="content-card create-session-card">
                <div className="form-group">
                    <label className="form-label">Game Mode</label>
                    <select
                        className="form-select"
                        value={mode}
                        onChange={(e) => setMode(e.target.value)}
                    >
                        <option value="competitive">Competitive (Individual Grids + Leaderboard)</option>
                        <option value="coop">Co-op (Shared Grid + Team Play)</option>
                    </select>
                    <p className="help-text">
                        {mode === 'competitive'
                            ? 'üèÜ Each player gets their own grid. Hints reduce your score.'
                            : 'ü§ù All players work on the same grid. Limited to 20 hints total.'}
                    </p>
                </div>

                <div className="form-group">
                    <label className="form-label">Timer Duration (minutes)</label>
                    <input
                        type="number"
                        className="form-input"
                        value={timerDuration}
                        onChange={(e) => setTimerDuration(Math.max(1, Math.min(60, parseInt(e.target.value) || 10)))}
                        min="1"
                        max="60"
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Words and Clues</label>
                    <textarea
                        className="form-input form-textarea"
                        value={wordsInput}
                        onChange={(e) => setWordsInput(e.target.value)}
                        placeholder="WORD|Clue&#10;PUZZLE|A game to solve&#10;CROSSWORD|Word game"
                        rows={10}
                    />
                    <p className="help-text">
                        Format: WORD|Clue (one per line). Min 3 words, max 30 words.
                    </p>
                </div>

                {error && <div className="error-message">{error}</div>}

                {!connected && (
                    <div className="error-message">
                        ‚ö†Ô∏è Not connected to server. Please wait...
                    </div>
                )}

                <div className="btn-group">
                    <button
                        className="btn-outline"
                        onClick={() => navigate('/')}
                        disabled={loading}
                    >
                        Cancel
                    </button>
                    <button
                        className="btn-primary"
                        onClick={handleCreate}
                        disabled={loading || !connected}
                    >
                        {loading ? <span className="loading-spinner"></span> : 'Create Session'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default CreateSession;
