import React, { useState, useEffect } from 'react';
import './GameControls.css';

function GameControls({
    timerDuration,
    startTime,
    onHintRequest,
    hintsRemaining,
    mode,
    totalHintsUsed,
    onGameComplete
}) {
    const [timeLeft, setTimeLeft] = useState(timerDuration);
    const [elapsedTime, setElapsedTime] = useState(0);

    useEffect(() => {
        if (!startTime) return;

        const interval = setInterval(() => {
            const now = Date.now();
            const elapsed = Math.floor((now - startTime) / 1000);
            const remaining = Math.max(0, timerDuration - elapsed);

            setElapsedTime(elapsed);
            setTimeLeft(remaining);

            if (remaining === 0) {
                clearInterval(interval);
                // Auto-complete game when time runs out
                if (onGameComplete) {
                    onGameComplete();
                }
            }
        }, 1000);

        return () => clearInterval(interval);
    }, [startTime, timerDuration, onGameComplete]);

    const formatTime = (seconds) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    const getTimePercentage = () => {
        return (timeLeft / timerDuration) * 100;
    };

    const getTimeColor = () => {
        const percentage = getTimePercentage();
        if (percentage > 50) return 'var(--success)';
        if (percentage > 25) return 'var(--warning)';
        return 'var(--error)';
    };

    return (
        <div className="game-controls">
            <div className="timer-section">
                <h3 className="control-heading">‚è±Ô∏è Time Remaining</h3>
                <div className="timer-display" style={{ color: getTimeColor() }}>
                    {formatTime(timeLeft)}
                </div>
                <div className="timer-bar">
                    <div
                        className="timer-progress"
                        style={{
                            width: `${getTimePercentage()}%`,
                            background: getTimeColor()
                        }}
                    />
                </div>
                <div className="elapsed-time">
                    Elapsed: {formatTime(elapsedTime)}
                </div>
            </div>

            <div className="hint-section">
                <h3 className="control-heading">üí° Hints</h3>
                {mode === 'competitive' ? (
                    <div className="hint-info">
                        <p className="hint-warning">
                            ‚ö†Ô∏è Each hint reduces your word score
                        </p>
                        <p className="hint-stats">
                            Hints used: <strong>{totalHintsUsed}</strong>
                        </p>
                    </div>
                ) : (
                    <div className="hint-info">
                        <p className="hint-remaining">
                            Remaining: <strong>{hintsRemaining}/20</strong>
                        </p>
                    </div>
                )}
                <button
                    className="btn-hint"
                    onClick={onHintRequest}
                    disabled={mode === 'coop' && hintsRemaining === 0}
                >
                    Request Hint
                </button>
            </div>

            <div className="actions-section">
                <button
                    className="btn-complete"
                    onClick={onGameComplete}
                >
                    Complete Game
                </button>
            </div>
        </div>
    );
}

export default GameControls;
