import React, { useEffect, useRef } from 'react';
import './SplashScreen.css';

const SplashScreen = ({ onComplete }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        // Cleanup
        canvas.innerHTML = '';

        const pinkPalette = ['#D81B60', '#AD1457', '#880E4F', '#F06292', '#ec407a'];

        // Config: Lager pixels = Fewer columns
        const COLUMNS = 20;
        const ROWS = 15; // Estimating for 16:9ish aspect ratio with big pixels
        const pixelCount = COLUMNS * ROWS;

        // CSS Variable update for dynamic grid
        canvas.style.gridTemplateColumns = `repeat(${COLUMNS}, 1fr)`;

        const pixels = [];

        // 1. Generate Pixels
        for (let i = 0; i < pixelCount; i++) {
            const pixel = document.createElement('div');
            pixel.classList.add('pixel');
            canvas.appendChild(pixel);
            pixels.push(pixel);
        }

        // Center coordinates for circular math
        const centerRow = ROWS / 2;
        const centerCol = COLUMNS / 2;

        // 2. Initial "Colorful Initialization" (Random Fade In)
        // Keeping random for the "Static/Noise" feel, but maybe slightly wave-like?
        // User said "circular like a tv coming on". Let's apply that to the transition mostly?
        // Or the pop-in too? Let's keep pop-in random-ish but faster to reduce "glitch" feel.
        pixels.forEach((pixel) => {
            setTimeout(() => {
                pixel.style.backgroundColor = pinkPalette[Math.floor(Math.random() * pinkPalette.length)];
                pixel.classList.add('pop');
            }, Math.random() * 1500);
        });

        // 3. The "White Out" Transition (Circular Wave)
        const whiteOutStart = 3000;

        const whiteOutTimeout = setTimeout(() => {
            pixels.forEach((pixel, index) => {
                const row = Math.floor(index / COLUMNS);
                const col = index % COLUMNS;

                // Circular Distance from Center
                const deltaX = col - centerCol;
                const deltaY = row - centerRow;
                const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

                // Delay based on distance (Center -> Outwards)
                // Multiply by 60ms for wave speed
                const delay = distance * 60;

                setTimeout(() => {
                    pixel.classList.add('white-out');
                }, delay);
            });
        }, whiteOutStart);

        // 4. End Transition
        // Max distance is corner to center. Sqrt(10^2 + 7.5^2) = ~12.5
        // Max delay = 12.5 * 60 = ~750ms.
        // Total wait = 3000 + 750 + 800 (transition) = ~4550ms.
        const completionTimeout = setTimeout(() => {
            if (onComplete) onComplete();
        }, whiteOutStart + 1500);

        return () => {
            clearTimeout(whiteOutTimeout);
            clearTimeout(completionTimeout);
            if (canvas) canvas.innerHTML = '';
        };
    }, [onComplete]);

    return (
        <div className="splash-screen-container">
            <div id="pixel-canvas" ref={canvasRef}></div>
        </div>
    );
};

export default SplashScreen;
