import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

// Components
import SplashScreen from './components/SplashScreen';
import MenuScreen from './components/MenuScreen';
import OnboardingScreen from './components/OnboardingScreen';
import GameScreen from './components/GameScreen';
import ResultsScreen from './components/ResultsScreen';

const SERVER_URL = 'http://localhost:3001';
const socket = io(SERVER_URL);

function App() {
    const [serverStatus, setServerStatus] = useState('Connecting...');
    const [currentScreen, setCurrentScreen] = useState('SPLASH'); // SPLASH, MENU, ONBOARDING, GAME, RESULTS
    const [bgImage, setBgImage] = useState('menu-background.jpg');
    const [isTransitioning, setIsTransitioning] = useState(false);
    const [onboardingTab, setOnboardingTab] = useState('CREATE');

    useEffect(() => {
        socket.on('connect', () => {
            setServerStatus('Connected');
        });

        socket.on('disconnect', () => {
            setServerStatus('Disconnected');
        });

        return () => {
            socket.off('connect');
            socket.off('disconnect');
        };
    }, []);

    // Navigation Handlers
    const handleSplashComplete = () => {
        setCurrentScreen('MENU');
        setBgImage('menu-background.jpg');
    };

    const handleStartOnboarding = (tab = 'CREATE') => {
        setIsTransitioning(true);
        setOnboardingTab(tab);
        // Wait for bottom bar to slide down before switching screen and BG
        setTimeout(() => {
            setCurrentScreen('ONBOARDING');
            setBgImage('menu-background2.jpg');
            setIsTransitioning(false);
        }, 800);
    };

    const handleOnboardingFinish = () => setCurrentScreen('GAME');
    const handleGameEnd = () => setCurrentScreen('RESULTS'); // Temp: Connect this to actual game end
    const handleBackToMenu = () => {
        setCurrentScreen('MENU');
        setBgImage('menu-background.jpg');
    };

    return (
        <div className="App">
            {/* Isolated Background Layer for Backdrop Blur Support */}
            <div
                className="bg-layer"
                style={{
                    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url(${process.env.PUBLIC_URL}/assets/${bgImage})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'top center',
                    backgroundRepeat: 'no-repeat',
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    width: '100vw',
                    height: '100vh',
                    transition: 'background-image 0.8s ease-in-out',
                    zIndex: -1
                }}
            />
            {/* Debug Status */}
            <div style={{ position: 'absolute', top: 0, right: 0, fontSize: '10px', padding: '5px', opacity: 0.5, color: '#fff', zIndex: 100 }}>
                Server: {serverStatus}
            </div>

            {currentScreen === 'SPLASH' && <SplashScreen onComplete={handleSplashComplete} />}
            {currentScreen === 'MENU' && <MenuScreen onStartOnboarding={handleStartOnboarding} isExiting={isTransitioning} />}
            {currentScreen === 'ONBOARDING' && <OnboardingScreen onFinish={handleOnboardingFinish} onCancel={handleBackToMenu} initialTab={onboardingTab} />}
            {currentScreen === 'GAME' && <GameScreen />}
            {currentScreen === 'RESULTS' && <ResultsScreen onBackToMenu={handleBackToMenu} />}
        </div>
    );
}

export default App;
