import React, { useState } from 'react';

const ClueList = () => {
    const [activeTab, setActiveTab] = useState('ACROSS');

    // Mock Clues
    const clues = {
        ACROSS: [
            { id: 1, number: 1, text: "A role gotten for producing memes on piggy" },
            { id: 2, number: 2, text: "A community member who is a known terror to scammers" },
            { id: 3, number: 3, text: "The loyal $PIGGY holder who never fails to curse others" },
            { id: 4, number: 4, text: "Another clue for across" },
            { id: 5, number: 5, text: "Yet another clue here" },
        ],
        DOWN: [
            { id: 6, number: 1, text: "Down clue number one is this" },
            { id: 7, number: 2, text: "Second down clue goes here" },
            { id: 8, number: 3, text: "Third one is the charm" },
        ]
    };

    return (
        <div className="clue-box">
            <div className="clue-tabs">
                <button
                    className={`clue-tab ${activeTab === 'DOWN' ? 'active' : ''}`}
                    onClick={() => setActiveTab('DOWN')}
                >
                    Down
                </button>
                <button
                    className={`clue-tab ${activeTab === 'ACROSS' ? 'active' : ''}`}
                    onClick={() => setActiveTab('ACROSS')}
                >
                    Across
                </button>
            </div>

            <div className="clue-list">
                {clues[activeTab].map((clue) => (
                    <div key={clue.id} className="clue-item">
                        {clue.number}. {clue.text}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ClueList;
