import React, { useEffect, useState, useMemo } from 'react';
import './SplashScreen.css';

const SplashScreen = ({ onComplete }) => {
    // Configuration
    const COLUMNS = 15;
    const ROWS = 12;
    const WAVE_SPEED = 70; // ms per unit distance (Lower = Faster wave)

    const pixels = useMemo(() => {
        const grid = [];
        // Palette: Bluish Purples as requested for the "Wave"
        const wavePalette = ['#7B1FA2', '#4A148C', '#CE93D8', '#AB47BC', '#8E24AA', '#536DFE', '#3D5AFE'];

        const centerRow = ROWS / 2;
        const centerCol = COLUMNS / 2;

        // Calculate max distance to know when animation finishes
        let maxDist = 0;

        for (let i = 0; i < COLUMNS * ROWS; i++) {
            const row = Math.floor(i / COLUMNS);
            const col = i % COLUMNS;

            // Distance Logic
            const deltaX = col - centerCol;
            const deltaY = row - centerRow;
            const dist = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

            if (dist > maxDist) maxDist = dist;

            // Simple Delay based on distance
            const delay = dist * WAVE_SPEED;

            grid.push({
                id: i,
                color: wavePalette[Math.floor(Math.random() * wavePalette.length)],
                delay: `${delay}ms`,
            });
        }
        return { grid, totalDuration: maxDist * WAVE_SPEED + 2500 }; // +2500 for animation duration
    }, []);

    useEffect(() => {
        // Timeline:
        // 0ms: Wave Starts
        // totalDuration: Wave Finishes (Pixels fade out)

        const completeTimer = setTimeout(() => {
            if (onComplete) onComplete();
        }, pixels.totalDuration + 500); // Small buffer

        return () => {
            clearTimeout(completeTimer);
        };
    }, [onComplete, pixels.totalDuration]);

    return (
        <div className="splash-screen-container">
            <div
                id="pixel-canvas"
                style={{ gridTemplateColumns: `repeat(${COLUMNS}, 1fr)` }}
            >
                {pixels.grid.map((p) => (
                    <div
                        key={p.id}
                        className="pixel"
                        style={{
                            '--pixel-color': p.color,
                            '--delay': p.delay
                        }}
                    ></div>
                ))}
            </div>
        </div>
    );
};

export default SplashScreen;
