import React from 'react';
import './Leaderboard.css';

function Leaderboard({ leaderboard, currentPlayerId }) {
    if (!leaderboard || leaderboard.length === 0) {
        return (
            <div className="leaderboard">
                <h3 className="leaderboard-title">üèÜ Leaderboard</h3>
                <p className="text-muted">No scores yet...</p>
            </div>
        );
    }

    const getRankEmoji = (rank) => {
        if (rank === 1) return 'ü•á';
        if (rank === 2) return 'ü•à';
        if (rank === 3) return 'ü•â';
        return `${rank}.`;
    };

    return (
        <div className="leaderboard">
            <h3 className="leaderboard-title">üèÜ Leaderboard</h3>
            <div className="leaderboard-list">
                {leaderboard.map((player) => {
                    const isCurrentPlayer = player.playerId === currentPlayerId;

                    return (
                        <div
                            key={player.playerId}
                            className={`leaderboard-item ${isCurrentPlayer ? 'current-player' : ''} ${player.rank <= 3 ? 'top-three' : ''
                                }`}
                        >
                            <span className="rank">{getRankEmoji(player.rank)}</span>
                            <span className="player-name">
                                {player.username}
                                {isCurrentPlayer && <span className="you-badge">You</span>}
                            </span>
                            <span className="score">{player.score.toFixed(2)}</span>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

export default Leaderboard;
