import React from 'react';
import './MenuScreen.css';

const MenuScreen = ({ onStartOnboarding }) => {
    return (
        <div className="menu-screen">
            <div className="menu-content">
                <header>
                    <h1 className="menu-title">Hogwash</h1>
                    <p className="menu-subtitle">WORDPLAY MULTIPLAYER</p>
                </header>

                <div className="menu-actions">
                    <button className="menu-btn primary" onClick={onStartOnboarding}>
                        Create Session
                    </button>
                    <button className="menu-btn secondary" onClick={() => alert('Join Session - Coming Soon')}>
                        Join Session
                    </button>
                </div>

                <footer style={{ marginTop: '1rem', fontSize: '0.8rem', opacity: 0.5 }}>
                    v1.0.0-beta
                </footer>
            </div>
        </div>
    );
};

export default MenuScreen;
