import React, { useState, useEffect } from 'react';
import './GameScreen.css';

// Components
import GameHeader from './GameHeader';
import CrosswordGrid from './CrosswordGrid';
import GameLeaderboard from './GameLeaderboard';
import ClueList from './ClueList';

const GameScreen = ({ socket, sessionId, sessionData, onGameEnd }) => {
    const [showLeaderboard, setShowLeaderboard] = useState(false);
    const [timeRemaining, setTimeRemaining] = useState(600); // 10 mins default
    const [grid, setGrid] = useState([]);
    const [clues, setClues] = useState([]);
    const [cursors, setCursors] = useState({});
    const [scores, setScores] = useState({});
    const [hintsUsed, setHintsUsed] = useState(0);

    useEffect(() => {
        if (!socket || !sessionId) return;

        // Request initial state (in case we joined after start or refreshed)
        socket.emit('CMD_REQUEST_GAME_STATE', { sessionId });

        socket.on('EVT_GAME_TICK', (data) => {
            setTimeRemaining(data.timeRemaining);
            if (data.cursors) setCursors(data.cursors);
            if (data.scores) setScores(data.scores);
            // Hints might be sent per player (object) or derived?
            // GameManager sends: hintsUsed: { playerId: count }
            // We need OUR hints.
            // But we don't know our ID efficiently here unless we check socket.id or playerId prop if passed.
            // GameScreen doesn't have playerId prop from App.js!
            // Wait, App.js passes (socket, sessionId, sessionData). 
            // sessionData has players. We can find our ID by matching socket.id? Not reliable if socket reconnects?
            // Actually, we usually rely on `socket.id`.
            if (data.hintsUsed && socket) {
                const count = data.hintsUsed[socket.id] || 0;
                setHintsUsed(count);
            }
        });

        socket.on('EVT_GRID_UPDATE', (data) => {
            setGrid(data.grid);
            if (data.clues) setClues(data.clues);
        });

        socket.on('EVT_GAME_OVER', (data) => {
            alert(data.message);
            onGameEnd(data.scores);
        });

        return () => {
            socket.off('EVT_GAME_TICK');
            socket.off('EVT_GRID_UPDATE');
            socket.off('EVT_GAME_OVER');
        };
    }, [socket, onGameEnd]);

    // Format Seconds into MM:SS
    const formatTime = (seconds) => {
        const m = Math.floor(seconds / 60).toString().padStart(2, '0');
        const s = (seconds % 60).toString().padStart(2, '0');
        return `${m}:${s}`;
    };

    return (
        <div className="game-screen fade-in">
            <GameHeader
                time={formatTime(timeRemaining)}
                hintsLeft={(sessionData?.settings?.hintLimit || 20) - hintsUsed}
                onThemeClick={onGameEnd}
                onLeaderboardClick={() => setShowLeaderboard(true)}
            />

            <div className="game-content">
                {/* Left: Grid Area */}
                <div className="game-grid-area">
                    <CrosswordGrid
                        grid={grid}
                        cursors={cursors}
                        socket={socket}
                        sessionId={sessionId}
                    />
                </div>

                {/* Right: Info Panel (Leaderboard + Clues) */}
                <div className="game-info-panel">
                    <GameLeaderboard
                        scores={scores}
                        players={sessionData ? sessionData.players : []}
                        mode={sessionData ? sessionData.settings.mode : 'Competitive'}
                    />
                    <ClueList clues={clues} />
                </div>
            </div>

            {/* Mobile Leaderboard Popup */}
            {showLeaderboard && (
                <div className="leaderboard-popup-overlay" onClick={() => setShowLeaderboard(false)}>
                    <div className="leaderboard-popup" onClick={(e) => e.stopPropagation()}>
                        <div className="popup-header">
                            <h3>LEADERBOARD</h3>
                            <button className="close-btn" onClick={() => setShowLeaderboard(false)}>Ã—</button>
                        </div>
                        <GameLeaderboard
                            scores={scores}
                            players={sessionData ? sessionData.players : []}
                            mode={sessionData ? sessionData.settings.mode : 'Competitive'}
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default GameScreen;
