import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import { useGameState } from '../hooks/useGameState';
import CrosswordGrid from '../components/CrosswordGrid';
import CluePanel from '../components/CluePanel';
import GameControls from '../components/GameControls';
import Leaderboard from '../components/Leaderboard';
import './Game.css';

function Game() {
    const { sessionId } = useParams();
    const location = useLocation();
    const navigate = useNavigate();
    const { socket, connected, on, off } = useSocket();

    const username = location.state?.username || 'Player';
    const sessionData = location.state?.session;

    const [mode, setMode] = useState(sessionData?.mode || 'competitive');
    const [startTime, setStartTime] = useState(null);
    const [selectedWord, setSelectedWord] = useState(null);
    const [playerId] = useState(Date.now().toString());

    const {
        gameState,
        leaderboard,
        hintsRemaining,
        initializeGame,
        updateCell,
        requestHint,
        checkWordComplete,
        completeWord,
        updateElapsedTime,
        completeGame,
    } = useGameState(socket, sessionId, mode);

    useEffect(() => {
        if (!socket || !connected) return;

        // Listen for game started event
        const handleGameStarted = (data) => {
            console.log('Game started:', data);
            setMode(data.mode);
            setStartTime(data.startedAt);
            initializeGame(data);
        };

        on('game-started', handleGameStarted);

        // If we have session data from lobby, initialize immediately
        if (sessionData && sessionData.crosswordData) {
            setMode(sessionData.mode);
            setStartTime(sessionData.startedAt || Date.now());
            initializeGame(sessionData);
        }

        return () => {
            off('game-started', handleGameStarted);
        };
    }, [socket, connected, sessionData, on, off, initializeGame]);

    const handleCellUpdate = (row, col, value) => {
        updateCell(row, col, value);

        // Check if any word is complete
        if (gameState.crosswordData) {
            gameState.crosswordData.words.forEach((word, index) => {
                if (checkWordComplete(index)) {
                    // Check if we haven't already marked this word as complete
                    const alreadyComplete = gameState.completedWords.some(
                        w => w.number === word.number && w.direction === word.direction
                    );

                    if (!alreadyComplete) {
                        completeWord(index);
                    }
                }
            });
        }
    };

    const handleHintRequest = () => {
        if (!selectedWord) {
            alert('Please select a word first by clicking on the grid');
            return;
        }

        // Find word index
        const wordIndex = gameState.crosswordData.words.findIndex(
            w => w.number === selectedWord.number && w.direction === selectedWord.direction
        );

        if (wordIndex === -1) return;

        requestHint(wordIndex, (response) => {
            if (!response.success) {
                alert(response.error || 'Failed to get hint');
            }
        });
    };

    const handleGameComplete = () => {
        const elapsed = Math.floor((Date.now() - startTime) / 1000);

        completeGame((response) => {
            if (response.success) {
                navigate(`/results/${sessionId}`, {
                    state: {
                        username,
                        scoreBreakdown: response.scoreBreakdown,
                        mode,
                    },
                });
            } else {
                alert('Failed to complete game: ' + (response.error || 'Unknown error'));
            }
        });
    };

    const handleClueClick = (word) => {
        setSelectedWord(word);
        // Could also scroll to the word in the grid
    };

    if (!gameState.crosswordData) {
        return (
            <div className="page-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Loading game...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="game-page">
            <div className="game-header">
                <h1 className="game-title">
                    {mode === 'competitive' ? 'üèÜ Competitive Mode' : 'ü§ù Co-op Mode'}
                </h1>
                <p className="game-subtitle">Session: {sessionId}</p>
            </div>

            <div className="game-container">
                <div className="game-main">
                    <div className="grid-section">
                        <CrosswordGrid
                            crosswordData={gameState.crosswordData}
                            gameState={gameState}
                            onCellUpdate={handleCellUpdate}
                            mode={mode}
                        />
                    </div>

                    <div className="clues-section">
                        <CluePanel
                            crosswordData={gameState.crosswordData}
                            selectedWord={selectedWord}
                            onClueClick={handleClueClick}
                        />
                    </div>
                </div>

                <div className="game-sidebar">
                    <GameControls
                        timerDuration={gameState.timerDuration}
                        startTime={startTime}
                        onHintRequest={handleHintRequest}
                        hintsRemaining={hintsRemaining}
                        mode={mode}
                        totalHintsUsed={gameState.hintsUsed}
                        onGameComplete={handleGameComplete}
                    />

                    {mode === 'competitive' && (
                        <Leaderboard
                            leaderboard={leaderboard}
                            currentPlayerId={playerId}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

export default Game;
