import React from 'react';

const GameLeaderboard = () => {
    // Mock data for now
    const players = [
        { rank: 1, name: "Monster18 (You)", score: 4800, solved: 6, isLocal: true },
        { rank: 2, name: "CryptoCat", score: 3800, solved: 8 },
        { rank: 3, name: "Metil", score: 2700, solved: 10 },
        { rank: 4, name: "Anhelink", score: 2690, solved: 5 },
        { rank: 5, name: "Nooxin", score: 2670, solved: 7 },
        { rank: 6, name: "D1ol", score: 2670, solved: 12 },
        { rank: 7, name: "Crouton.hl", score: 2530, solved: 3 },
        { rank: 8, name: "RevengeIP", score: 2420, solved: 6 },
        { rank: 9, name: "Pa3gBa", score: 2210, solved: 3 },
        { rank: 10, name: "Cryptoholic", score: 2090, solved: 2 },
    ];

    return (
        <div className="leaderboard-container">
            <div className="leaderboard-header">
                <div className="lb-col rank">Rank</div>
                <div className="lb-col name">Name</div>
                <div className="lb-col score">Score</div>
                <div className="lb-col solved">Words</div>
            </div>

            <div className="leaderboard-list">
                {players.map((p) => (
                    <div key={p.rank} className={`lb-row ${p.isLocal ? 'highlight' : ''}`}>
                        <div className="lb-col rank">{p.rank === 1 ? '1st' : p.rank === 2 ? '2nd' : p.rank === 3 ? '3rd' : `${p.rank}th`}</div>
                        <div className="lb-col name">{p.name}</div>
                        <div className="lb-col score">{p.score}</div>
                        <div className="lb-col solved">{p.solved}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default GameLeaderboard;
