import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import './Lobby.css';

function Lobby() {
    const { sessionId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const { emit, on, off, connected } = useSocket();

    const username = location.state?.username || 'Player';
    const isHost = location.state?.isHost || false;
    const sessionData = location.state?.session;

    const [players, setPlayers] = useState([]);
    const [session, setSession] = useState(sessionData || null);
    const [loading, setLoading] = useState(!sessionData);
    const [error, setError] = useState('');

    useEffect(() => {
        if (connected) {
            // Get or create persistent player ID
            let storedPlayerId = localStorage.getItem('playerId');
            if (!storedPlayerId) {
                storedPlayerId = Date.now().toString();
                localStorage.setItem('playerId', storedPlayerId);
            }

            // Always join session to ensure socket is in the room
            emit('join-session', {
                sessionId,
                playerId: storedPlayerId,
                username,
            }, (response) => {
                setLoading(false);
                if (response.success) {
                    setSession(response.gameState);
                    // Normalize DB players (player_id) to client structure (playerId)
                    const initialPlayers = (response.players || []).map(p => ({
                        ...p,
                        playerId: p.player_id || p.playerId, // Handle both cases just to be safe
                        player_id: undefined // Cleanup mixed keys
                    }));
                    setPlayers(initialPlayers);
                } else {
                    // If host created it but socket wasn't in room, this fixes it.
                    // If error is "session full" but we are already in db under same ID, it should be fine?
                    // distinct on conflict update handles it.
                    if (response.error && !response.error.includes('already')) {
                        setError(response.error || 'Failed to join session');
                    }
                }
            });
        }

        // Listen for player events
        const handlePlayerJoined = (data) => {
            console.log('üîµ [Lobby] player-joined event received:', data);
            setPlayers((prev) => {
                const newId = (data.playerId || data.player_id)?.toString();
                console.log('   [Lobby] Current players:', prev);
                console.log('   [Lobby] Checking for existance of ID:', newId);

                if (prev.some(p => (p.playerId || p.player_id)?.toString() === newId)) {
                    console.log('   [Lobby] Player already exists, ignoring.');
                    return prev;
                }
                console.log('   [Lobby] Adding new player.');
                return [...prev, { ...data, playerId: newId }];
            });
        };

        const handlePlayerLeft = (data) => {
            setPlayers((prev) => prev.filter((p) => p.playerId !== data.playerId));
        };

        // Handle disconnects same as leaving for now to prevent ghosting
        const handlePlayerDisconnected = (data) => {
            setPlayers((prev) => prev.filter((p) => p.playerId !== data.playerId));
        };

        const handleGameStarted = (data) => {
            navigate(`/game/${sessionId}`, {
                state: { username, session: data },
            });
        };

        on('player-joined', handlePlayerJoined);
        on('player-left', handlePlayerLeft);
        on('player-disconnected', handlePlayerDisconnected);
        on('game-started', handleGameStarted);

        return () => {
            off('player-joined', handlePlayerJoined);
            off('player-left', handlePlayerLeft);
            off('player-disconnected', handlePlayerDisconnected);
            off('game-started', handleGameStarted);
        };
    }, [sessionId, username, sessionData, connected, emit, on, off, navigate]);

    const handleStartGame = () => {
        emit('start-game', { sessionId }, (response) => {
            if (!response.success) {
                setError(response.error || 'Failed to start game');
            }
        });
    };

    const handleCopySessionId = () => {
        navigator.clipboard.writeText(sessionId);
        alert('Session ID copied to clipboard!');
    };

    if (loading) {
        return (
            <div className="page-container">
                <div className="loading-spinner"></div>
                <p>Joining session...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="page-container">
                <div className="content-card">
                    <h2>Error</h2>
                    <p className="error-message">{error}</p>
                    <button className="btn-primary" onClick={() => navigate('/')}>
                        Go Home
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="page-container">
            <div className="page-header">
                <h1 className="page-title">Game Lobby</h1>
                <p className="page-subtitle">Waiting for players...</p>
            </div>

            <div className="lobby-container">
                <div className="lobby-card">
                    <div className="session-info">
                        <h3>Session ID</h3>
                        <div className="session-id-display">
                            <code>{sessionId}</code>
                            <button className="btn-copy" onClick={handleCopySessionId}>
                                üìã Copy
                            </button>
                        </div>
                    </div>

                    <div className="game-info">
                        <div className="info-item">
                            <span className="info-label">Mode:</span>
                            <span className="info-value">
                                {session?.mode === 'competitive' ? 'üèÜ Competitive' : 'ü§ù Co-op'}
                            </span>
                        </div>
                        <div className="info-item">
                            <span className="info-label">Timer:</span>
                            <span className="info-value">
                                {session?.timerDuration ? `${Math.floor(session.timerDuration / 60)} minutes` : 'N/A'}
                            </span>
                        </div>
                        <div className="info-item">
                            <span className="info-label">Words:</span>
                            <span className="info-value">
                                {sessionData?.wordCount || 'Loading...'}
                            </span>
                        </div>
                    </div>

                    <div className="players-section">
                        <h3>Players ({players.length}/30)</h3>
                        <div className="players-list">
                            {players.map((player, index) => (
                                <div key={player.playerId || index} className="player-item">
                                    <span className="player-icon">üë§</span>
                                    <span className="player-name">{player.username}</span>
                                    {player.playerId === players[0]?.playerId && (
                                        <span className="host-badge">Host</span>
                                    )}
                                </div>
                            ))}
                            {players.length === 0 && (
                                <p className="text-muted">No players yet...</p>
                            )}
                        </div>
                    </div>

                    {error && <div className="error-message">{error}</div>}

                    <div className="lobby-actions">
                        {isHost ? (
                            <button
                                className="btn-primary btn-full"
                                onClick={handleStartGame}
                                disabled={players.length === 0}
                            >
                                Start Game
                            </button>
                        ) : (
                            <p className="text-muted">Waiting for host to start the game...</p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Lobby;
